		<form action="/" class="w-[30%] flex flex-row gap-4">
			<input on:input={(e) => setItem("q", e)} value={Cookies.get("q") || ""} type="text" class="rounded-lg w-full bg-slate-50 px-4 py-2 border border-slate-300 outline-none hover:bg-slate-100 focus:border-slate-400 focus:bg-slate-100 transition-colors" placeholder="Search...">
			<select on:change={(e) => {
					setItem("db", e)
					window.location.reload()
				}} class="rounded-lg bg-slate-50 px-4 py-2 border border-slate-300 outline-none">
				{#each lockFile.downloads as download}
					<option value={download.database} selected={Cookies.get("db") == download.database}>{download.name}</option>
				{/each}
			</select>
